import { execSync } from 'node:child_process';
import { d as defineCommand, c as consola, a as colors } from '../shared/nuxi.57fa43de.mjs';
import 'node:util';
import 'node:path';
import 'node:process';
import 'node:tty';
import { r as readPackageJSON } from '../shared/nuxi.8d9be659.mjs';
import { g as getPackageManager, p as packageManagerLocks } from '../shared/nuxi.0fe95894.mjs';
import { r as rmRecursive, t as touchFile } from '../shared/nuxi.e016df24.mjs';
import { c as cleanupNuxtDirs, n as nuxtVersionToGitIdentifier } from '../shared/nuxi.2c92dc7e.mjs';
import { s as sharedArgs, l as legacyRootDirArgs } from '../shared/nuxi.01862219.mjs';
